runTest("trivial eq / neq", function()
    assertTrue("abc" == "abc")
    assertTrue("abc" ~= "def")
end)

runTest("other binary operators", function()
    assertEqual(25 or "foobar", 25)
    assertEqual("hello" and 5, 5)
    assertTrue(1 < 2)
    assertTrue(2 > 1)
    assertTrue(1 <= 1)
    assertTrue(1 <= 2)
    assertTrue(2 >= 1)
    assertTrue(2 >= 2)
    assertTrue(1 ~= 9)
    assertTrue("a" == "a")
    assertEqual(5 | 2, 7)
    assertEqual(~5, -6)
    assertEqual(7 & 3, 3)
    assertEqual(1 << 4, 16)
    assertEqual(9 >> 3, 1)
    assertEqual("a" .. "bc", "abc")
    assertEqual(4 + 5, 9)
    assertEqual(4 - 5, -1)
    assertEqual(9 * 8, 72)
    assertEqual(50 / 20, 2.5)
    assertEqual(50 // 20, 2)
    assertEqual(11 % 5, 1)
end)

runTest("coercion", function()
    assertEqual("1" << "4", 16)
end)